<?php
$apache_version = apache_get_version();
$splits = explode('/',$apache_version);
$apache_version_no = $splits[1];
$php_version = phpversion();

//find_mysql_version()
$output = shell_exec('mysql -V');
preg_match('@[0-9]+\.[0-9]+\.[0-9]+@', $output, $version);
$mysqlversion = $version[0];

$zend_extensions = false;
$extension_list = '';
$extensions= get_loaded_extensions ($zend_extensions);
foreach ($extensions as $extension) {
	$extension_list.='<li>'.$extension.'</li>';
}

if (isset($_GET['phpinfo'])){
	phpinfo();
	exit();
}

$project_list = '';
foreach(glob('*', GLOB_ONLYDIR) as $dir) {
	$project_list .= '<li><a href="'.$dir.'" target="_blank"><b>'.$dir.'</b></a></li>';
}

